import React, { useState, useEffect, useRef, useReducer } from "react";


const About = () => {

  return (
    <div className="Container">
      <h1>About this Application</h1>
      <p>The purpose of this application is to demonstrate the use of React, React state using Context API and capturing data from external sources .</p>
      <p>This application uses React with the context API to manage state.</p>
      <p>
        Data is collected from the Github API demonstrating the use of
        asynchronous API queries
      </p>
    </div>
  );
};

export default About;
